import React, { Component } from 'react'
import './App.css'
import Navbar from './Navbar'
import Web3 from 'web3'

class App extends Component {

    async Web3() {
        if(window.ethereuem) {
            window.Web3 =new Web3(window.ethereuem)
            await window.ethereuem.enable()
        } else if(window.Web3) {
            window.web3 = new Web3(window.web3.currenProvider)
        } else{
            window.alert('No ethereum browser detected!You can checkout the Metamask')
        }
    }

    constructor(props) {
        super(props)
        this.state ={
            account: '0x0'
        }
    }

    render() {
        return (
        <div>
            <Navbar account={this.state.account}/>
            
                
        </div>
           
        )
    }

}
export default App;
